import React, { createContext, useContext, useState, useEffect } from 'react';

const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [tableId, setTableId] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  // Khi mount, load tableId từ localStorage nếu có
  useEffect(() => {
    const storedTable = localStorage.getItem('tableId');
    if (storedTable) {
      setTableId(storedTable);
    }
  }, []);

  // Khi tableId thay đổi, lưu vào localStorage
  useEffect(() => {
    if (tableId) {
      localStorage.setItem('tableId', tableId);
    }
  }, [tableId]);

  const addToCart = (food) => {
    setIsLoading(true);
    setCartItems(prev => {
      const existing = prev.find(item => item.id === (food.id || food._id));
      if (existing) {
        return prev.map(item =>
          (item.id === (food.id || food._id))
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      }
      // chuẩn hóa id
      const id = food.id || food._id;
      return [...prev, { ...food, id, quantity: 1 }];
    });
    setIsLoading(false);
  };

  const removeFromCart = (foodId) => {
    setCartItems(prev => prev.filter(item => item.id !== foodId));
  };

  const updateQuantity = (foodId, newQuantity) => {
    if (newQuantity <= 0) {
      return removeFromCart(foodId);
    }
    setCartItems(prev => prev.map(item =>
      item.id === foodId
        ? { ...item, quantity: newQuantity }
        : item
    ));
  };

  const clearCart = () => {
    setCartItems([]);
    setTableId(null);
    localStorage.removeItem('tableId');
  };

  const getTotalPrice = () =>
    cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  const getTotalItems = () =>
    cartItems.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <CartContext.Provider value={{
      tableId,
      setTableId,
      cartItems,
      addToCart,
      removeFromCart,
      updateQuantity,
      clearCart,
      getTotalPrice,
      getTotalItems,
      isLoading
    }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
