import { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import Menu from './pages/Menu';
import CartModal from './components/CartModal';
import { CartProvider } from './contexts/CartContext';
import { AuthContext } from './contexts/AuthContext';
import Dashboard from './components/Dashboard';
import FoodManagement from './components/FoodManagement';
import OrderManagement from './components/OrderManagement';
import UserManagement from './components/UserManagement';
import { BarChart3, UtensilsCrossed, ShoppingBag, Users } from 'lucide-react';

import { useState,useContext,  } from 'react';
import Login from './pages/Login';
import Register from './pages/Register';

// Component xử lý token từ URL
function TokenHandler() {
  const navigate = useNavigate();
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get('token');
    if (token) {
      localStorage.setItem('token', token);
      window.history.replaceState({}, document.title, "/");
      navigate('/');
    }
  }, [navigate]);
  return null;
}

const AdminLayout = () => {
  const [tab, setTab] = useState('dashboard');
  const tabs = [
    { id: 'dashboard', label: 'Dashboard', icon: BarChart3 },
    { id: 'foods', label: 'Foods', icon: UtensilsCrossed },
    { id: 'orders', label: 'Orders', icon: ShoppingBag },
    { id: 'users', label: 'Users', icon: Users }
  ];

  const renderTab = {
    dashboard: <Dashboard />,
    foods: <FoodManagement />,
    orders: <OrderManagement />,
    users: <UserManagement />
  }[tab];

  return (
    <div className="container-fluid">
      <div className="row">
        <nav className="col-12 col-md-3 col-lg-2 bg-light sidebar py-3">
          <h4 className="px-3">Restaurant Admin</h4>
          <ul className="nav flex-column">
            {tabs.map(t => (
              <li className="nav-item" key={t.id}>
                <button
                  className={`nav-link btn btn-link w-100 text-start ${tab === t.id ? 'active text-primary' : ''}`}
                  onClick={() => setTab(t.id)}
                >
                  <t.icon className="me-2" size={18} />
                  {t.label}
                </button>
              </li>
            ))}
          </ul>
        </nav>
        <main className="col-12 col-md-9 col-lg-10 px-4 py-3">
          <h2 className="mb-4">{tabs.find(t => t.id === tab).label}</h2>
          {renderTab}
        </main>
      </div>
    </div>
  );
};

const AdminRoute = ({ children }) => {
  const { user, loading } = useContext(AuthContext);
  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }
  if (!user || user.role !== 'admin') {
    return <Navigate to="/" replace />;
  }
  return children;
};

function Layout({ children }) {
  return (
    <>
      {children}
      <CartModal />
    </>
  );
}

function App() {
  return (
    <CartProvider>
      <Router>
        <TokenHandler />
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/menu" element={
            <Layout>
              <Menu />
            </Layout>
          } />
        </Routes>
      </Router>
    </CartProvider>
  );
}

export default App;
