import React, { useContext, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import { AuthContext } from '../contexts/AuthContext';
import { apiService } from '../service/apiService';
import { cleanId } from '../utils/cleanId';

export default function Checkout() {
  const { tableId: rawParam } = useParams();
  const { tableId: rawContext, cartItems, clearCart, getTotalPrice } = useCart();
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  // Clean trước khi dùng
  const paramTableId = cleanId(rawParam);
  const contextTableId = cleanId(rawContext);
  const tableId = paramTableId || contextTableId || cleanId(localStorage.getItem('tableId'));

  const handlePay = async () => {
    if (!user?._id) {
      alert('Bạn cần đăng nhập để đặt hàng!');
      return;
    }
    if (!tableId) {
      alert('Không xác định được bàn để đặt hàng!');
      return;
    }
    if (!cartItems.length) {
      alert('Giỏ hàng đang trống!');
      return;
    }

    // Gửi đúng userId & tableId
    const orderData = {
      userId: user._id,
      tableId,
      items: cartItems.map(item => ({
        food: item._id || item.id,
        quantity: item.quantity
      })),
      totalAmount: getTotalPrice(),
      status: 'Đang xử lý'
    };

    console.log('Gửi orderData:', orderData);

    setLoading(true);
    try {
      const newOrder = await apiService.createOrder(orderData);
      // Nếu muốn show chi tiết newOrder, có thể truyền state qua navigate
      clearCart();
      navigate('/success', { state: { order: newOrder } });
    } catch (error) {
      console.error('Thanh toán thất bại, response:', error.message);
      alert('Thanh toán thất bại. Vui lòng thử lại.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="checkout-container">
      <h2>Thanh toán (Bàn {tableId})</h2>

      <ul>
        {cartItems.map(item => (
          <li key={item.id}>
            {item.name} x {item.quantity} = {(item.price * item.quantity).toLocaleString()}₫
          </li>
        ))}
      </ul>

      <h4>Tổng cộng: {getTotalPrice().toLocaleString()}₫</h4>

      <button
        onClick={handlePay}
        disabled={loading || !cartItems.length}
      >
        {loading ? 'Đang xử lý...' : 'Xác nhận và thanh toán'}
      </button>
    </div>
  );
}
