import React, { useEffect, useState } from "react";

const Home = () => {
  const [email, setEmail] = useState("");
  const [foods, setFoods] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const payload = JSON.parse(atob(token.split('.')[1]));
        setEmail(payload.email);
      } catch (e) {
        setEmail("");
      }
    }
    // Gọi API lấy danh sách món ăn
    fetch("http://localhost:4000/api/food")
      .then(res => res.json())
      .then(data => setFoods(data))
      .catch(() => setFoods([]));
  }, []);

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: 100 }}>
      <h2>Trang chủ</h2>
      {email ? (
        <>
          <p>Đăng nhập thành công với Gmail: <b>{email}</b></p>
          <button onClick={() => { localStorage.removeItem('token'); window.location.reload(); }}>Đăng xuất</button>
        </>
      ) : (
        <button onClick={() => window.location.href = '/login'}>Đăng nhập</button>
      )}
      <h3 style={{ marginTop: 40 }}>Danh sách món ăn</h3>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {foods.map(food => (
          <li key={food._id} style={{ border: '1px solid #ccc', margin: 8, padding: 12, borderRadius: 8, minWidth: 300 }}>
            <b>{food.name}</b> - {food.price}đ
            <div>{food.description}</div>
            {food.image && <img src={food.image} alt={food.name} style={{ maxWidth: 120, marginTop: 8 }} />}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
