import React, { createContext, useContext, useState} from 'react';


const CartContext = createContext();

const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [tableId, setTableId] = useState(null);


  const addToCart = async (food) => {
    setIsLoading(true);
    try {
      const existing = cartItems.find(item => item.id === food.id);
      let updated;
      if (existing) {
        updated = cartItems.map(item =>
          item.id === food.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        updated = [...cartItems, { ...food, quantity: 1 }];
      }
      setCartItems(updated);
      // (không gọi API ngay, đợi Checkout)
    } catch (err) {
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  };

  const removeFromCart = (foodId) => {
    setCartItems(cartItems.filter(item => item.id !== foodId));
  };

  const updateQuantity = (foodId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(foodId);
      return;
    }
    
    setCartItems(cartItems.map(item =>
      item.id === foodId 
        ? { ...item, quantity: newQuantity }
        : item
    ));
  };

  const clearCart = () => {
    setCartItems([]);
    setTableId(null);
    localStorage.removeItem('tableId'); // Xóa tableId khỏi localStorage
    
  };

  const getTotalPrice = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getTotalItems = () => {
    return cartItems.reduce((total, item) => total + item.quantity, 0);
  };

  return (
    <CartContext.Provider value={{
      cartItems,
      addToCart,
      removeFromCart,
      updateQuantity,
      clearCart,
      getTotalPrice,
      getTotalItems,
      isLoading
    }}>
      {children}
    </CartContext.Provider>
  );
};

const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export { CartProvider, useCart, CartContext };
