import React, { useState, useEffect } from 'react';
import FoodCard from '../components/FoodCard';
import CategoryFilter from '../components/CategoryFilter';
import 'bootstrap/dist/css/bootstrap.min.css';
import {apiService} from "../service/apiService" 
import Header from '../components/Header';
import Footer from '../components/Footer';
import Hero from '../components/Hero';


const Menu = () => {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await apiService.getCategories();
        setCategories(response);
      } catch (err) {
        console.error('Lỗi khi tải danh mục:', err);
      }
    };

    fetchCategories();
  }, []);

  // Load danh sách món ăn theo danh mục
  useEffect(() => {
    const fetchFoods = async () => {
      try {
        const params = selectedCategory !== 'all' ? { category: selectedCategory } : {};
        const response = await apiService.getFoods(params);
        setFoods(response);
      } catch (err) {
        console.error('Lỗi khi tải món ăn:', err);
      }
    };

    fetchFoods();
  }, [selectedCategory]);

  if (loading) {
    return (
      <div className="container py-5">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    
    <section id="menu" className="py-5">
      <Header />
      <Hero />
      <div className="container">
        <div className="row">
          <div className="col-12 text-center mb-5">
            <h2 className="display-5 fw-bold">Our Menu</h2>
            <p className="lead">Discover our delicious selection of dishes</p>
          </div>
        </div>

        <CategoryFilter
            categories={categories}
            selectedCategory={selectedCategory}
            onCategoryChange={setSelectedCategory}
        />
        <div className="row">
            {foods.length > 0 ? (
            foods.map((food) => (
                <div className="col-md-4 mb-4" key={food._id}>
                <FoodCard food={food} />
                </div>
            ))
            ) : (
            <p>Không có món nào được tìm thấy.</p>
            )}
        </div>
      </div>
      <Footer />
    </section>
  );
};

export default Menu;
