import React, { useState } from 'react';
import { useCart } from '../contexts/CartContext';
import { Plus, Minus, X } from 'lucide-react';
import {apiService} from '../service/apiService';
import bootstrap from 'bootstrap/dist/js/bootstrap.bundle.min.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useNavigate } from 'react-router-dom';

const CartModal = () => {
  const { cartItems, removeFromCart, updateQuantity, clearCart, getTotalPrice } = useCart();
  const [isOrdering, setIsOrdering] = useState(false);

  const handleOrder = async () => {
  setIsOrdering(true);
  try {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user || !user._id) {
      alert('Bạn cần đăng nhập để đặt hàng!');
      return;
    }

    const orderData = {
      userId: user._id,
      items: cartItems.map(item => ({
        foodId: item.id,
        quantity: item.quantity,
        price: item.price
      })),
      total: getTotalPrice(),
      status: 'pending'
    };

    // Gọi API thật để tạo đơn hàng
    await apiService.createOrder(orderData);

    alert('Đặt hàng thành công!');
    clearCart();

    // Đóng modal
    const modal = document.getElementById('cartModal');
    const bootstrapModal = bootstrap.Modal.getInstance(modal);
    bootstrapModal?.hide();
  } catch (error) {
    console.error('Lỗi khi đặt hàng:', error);
    alert('Đặt hàng thất bại. Vui lòng thử lại sau.');
  } finally {
    setIsOrdering(false);
  }
};

  return (
    <div className="modal fade" id="cartModal" tabIndex="-1">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Shopping Cart</h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal"></button>
          </div>
          <div className="modal-body">
            {cartItems.length === 0 ? (
              <p className="text-center text-muted">Your cart is empty</p>
            ) : (
              <div>
                {cartItems.map(item => (
                  <div key={item.id} className="d-flex align-items-center border-bottom pb-3 mb-3">
                    <img 
                      src={item.image || "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=100&h=100&fit=crop"} 
                      alt={item.name}
                      className="rounded me-3"
                      style={{ width: '60px', height: '60px', objectFit: 'cover' }}
                    />
                    <div className="flex-grow-1">
                      <h6 className="mb-1">{item.name}</h6>
                      <p className="text-muted mb-0">${item.price}</p>
                    </div>
                    <div className="d-flex align-items-center me-3">
                      <button 
                        className="btn btn-sm btn-outline-secondary"
                        onClick={() => updateQuantity(item.id, item.quantity - 1)}
                      >
                        <Minus size={14} />
                      </button>
                      <span className="mx-2">{item.quantity}</span>
                      <button 
                        className="btn btn-sm btn-outline-secondary"
                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                      >
                        <Plus size={14} />
                      </button>
                    </div>
                    <button 
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => removeFromCart(item.id)}
                    >
                      <X size={14} />
                    </button>
                  </div>
                ))}
                <div className="d-flex justify-content-between align-items-center">
                  <h5>Total: ${getTotalPrice().toFixed(2)}</h5>
                  <button 
                    className="btn btn-outline-secondary"
                    onClick={clearCart}
                  >
                    Clear Cart
                  </button>
                </div>
              </div>
            )}
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">
              Close
            </button>
            {cartItems.length > 0 && (
              <button 
                type="button" 
                className="btn btn-primary"
                onClick={handleOrder}
                disabled={isOrdering}
              >
                {isOrdering ? 'Placing Order...' : 'Place Order'}
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartModal;
