import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import FoodCard from '../components/FoodCard';
import CategoryFilter from '../components/CategoryFilter';
import { useCart } from '../contexts/CartContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import { apiService } from '../service/apiService';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Hero from '../components/Hero';

const Menu = () => {
  const { tableId: paramTableId } = useParams();
  const navigate = useNavigate();
  const { tableId: contextTableId, setTableId } = useCart();

  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Lấy giá trị tableId ưu tiên: URL -> Context -> localStorage
    const stored = localStorage.getItem('tableId');
    const t = paramTableId || contextTableId || stored;

    if (!t) {
      navigate('/scan');
      return;
    }

    setTableId(t);
    localStorage.setItem('tableId', t);

    // Tải dữ liệu menu
    const loadData = async () => {
      try {
        const [foodsData, categoriesData] = await Promise.all([
          apiService.getFoods(),
          apiService.getCategories(),
        ]);
        setFoods(foodsData);
        setCategories(categoriesData);
      } catch (error) {
        console.error('Lỗi khi tải menu:', error);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [paramTableId, contextTableId, navigate, setTableId]);

  const filteredFoods =
    selectedCategory === 'all'
      ? foods
      : foods.filter((f) => f.category === selectedCategory);

  if (loading) {
    return (
      <div className="container py-5 text-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <section id="menu" className="py-5">
      <Header />
      <Hero />
      <div className="container">
        <div className="text-center mb-5">
          <h2 className="display-5 fw-bold">Thực đơn của chúng tôi</h2>
          <p className="lead">Khám phá những món ăn ngon của chúng tôi</p>
        </div>

        <CategoryFilter
          categories={categories}
          selectedCategory={selectedCategory}
          onCategoryChange={setSelectedCategory}
        />

        <div className="row mt-4">
          {filteredFoods.length > 0 ? (
            filteredFoods.map((food) => (
              <FoodCard key={food._id || food.id} food={food} />
            ))
          ) : (
            <p className="text-muted text-center">Không tìm thấy món ăn.</p>
          )}
        </div>
      </div>
      <Footer />
    </section>
  );
};

export default Menu;
