import React, { useState, useEffect, useContext } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import FoodCard from '../components/FoodCard';
import CategoryFilter from '../components/CategoryFilter';
import MenuFilters from '../components/MenuFilters';
import { useCart } from '../contexts/CartContext';
import { AuthContext } from '../contexts/AuthContext';
import { cleanId } from '../utils/cleanId';
import 'bootstrap/dist/css/bootstrap.min.css';
import { apiService } from '../service/apiService';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Hero from '../components/Hero';

const Menu = () => {
  const { tableId: paramTableId } = useParams();
  const navigate = useNavigate();
  const { tableId: contextTableId, setTableId } = useCart();
  const { user, loading } = useContext(AuthContext);

  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [priceRange, setPriceRange] = useState({ min: 0, max: 1000000 });
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [menuLoading, setMenuLoading] = useState(true);
  const [viewMode, setViewMode] = useState('grid'); // grid or list

  useEffect(() => {
    // Đợi AuthContext load xong
    if (loading) return;

    // Lấy giá trị tableId ưu tiên: URL -> Context -> localStorage
    const rawParam = paramTableId;
    const rawContext = contextTableId;
    const rawStorage = localStorage.getItem('tableId');
    const p = cleanId(rawParam);
    const c = cleanId(rawContext);
    const s = cleanId(rawStorage);
    const t = p || c || s;

    if (!t) {
      navigate('/');
      return;
    }

    // Kiểm tra authentication - nếu chưa đăng nhập và có tableId thì redirect đến login
    if (!user && t) {
      navigate('/login', {
        state: { 
          from: { pathname: `/menu/${t}` },
          tableId: t 
        }
      });
      return;
    }

    setTableId(t);
    localStorage.setItem('tableId', t);

    // Tải dữ liệu menu
    const loadData = async () => {
      try {
        const [foodsData, categoriesData] = await Promise.all([
          apiService.getFoods(),
          apiService.getCategories(),
        ]);
        setFoods(foodsData);
        setCategories(categoriesData);
        
        // Tự động set price range dựa trên dữ liệu
        if (foodsData.length > 0) {
          const prices = foodsData.map(food => food.price);
          const minPrice = Math.min(...prices);
          const maxPrice = Math.max(...prices);
          setPriceRange({ min: minPrice, max: maxPrice });
        }
      } catch (error) {
        console.error('Lỗi khi tải menu:', error);
      } finally {
        setMenuLoading(false);
      }
    };

    loadData();
  }, [paramTableId, contextTableId, navigate, setTableId, user, loading]);

  // Filter và sort foods
  const filteredAndSortedFoods = React.useMemo(() => {
    let result = foods;

    // Filter by category
    if (selectedCategory !== 'all') {
      result = result.filter(food => food.category === selectedCategory);
    }

    // Filter by price range
    result = result.filter(food => 
      food.price >= priceRange.min && food.price <= priceRange.max
    );

    // Filter by search term
    if (searchTerm) {
      result = result.filter(food => 
        food.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (food.description && food.description.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Sort
    result.sort((a, b) => {
      switch (sortBy) {
        case 'price-low':
          return a.price - b.price;
        case 'price-high':
          return b.price - a.price;
        case 'name':
          return a.name.localeCompare(b.name);
        default:
          return 0;
      }
    });

    return result;
  }, [foods, selectedCategory, priceRange, searchTerm, sortBy]);

  const maxPrice = foods.length > 0 ? Math.max(...foods.map(f => f.price)) : 1000000;

  if (loading) {
    return (
      <div className="min-vh-100 d-flex align-items-center justify-content-center" 
           style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
        <div className="text-center text-white">
          <div className="spinner-border mb-3" style={{ width: '3rem', height: '3rem' }}>
            <span className="visually-hidden">Loading...</span>
          </div>
          <h4>Đang kiểm tra đăng nhập...</h4>
        </div>
      </div>
    );
  }

  // Hiển thị loading khi đang tải dữ liệu menu
  if (menuLoading) {
    return (
      <div className="min-vh-100 d-flex align-items-center justify-content-center" 
           style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
        <div className="text-center text-white">
          <div className="spinner-border mb-3" style={{ width: '3rem', height: '3rem' }}>
            <span className="visually-hidden">Loading...</span>
          </div>
          <h4>Đang tải thực đơn...</h4>
        </div>
      </div>
    );
  }

  return (
    <>
      <Header />
      <Hero />
      
      {/* Menu Section */}
      <section className="py-5" style={{ backgroundColor: '#f8f9fa' }}>
        <div className="container">
          {/* Header */}
          <div className="text-center mb-5">
            <h2 className="display-4 fw-bold text-dark mb-3">Thực đơn của chúng tôi</h2>
            <p className="lead text-muted">Khám phá những món ăn ngon với hương vị tuyệt vời</p>
            <div className="d-inline-block px-4 py-2 bg-primary text-white rounded-pill">
              <i className="fas fa-utensils me-2"></i>
              {filteredAndSortedFoods.length} món ăn
            </div>
          </div>

          {/* Filters */}
          <MenuFilters
            searchTerm={searchTerm}
            setSearchTerm={setSearchTerm}
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
            categories={categories}
            sortBy={sortBy}
            setSortBy={setSortBy}
            viewMode={viewMode}
            setViewMode={setViewMode}
            priceRange={priceRange}
            setPriceRange={setPriceRange}
            maxPrice={maxPrice}
          />
          <div className="d-flex justify-content-between align-items-center mb-4">
            <div>
              <h5 className="mb-0">
                {filteredAndSortedFoods.length > 0 ? (
                  <>Tìm thấy <span className="text-primary fw-bold">{filteredAndSortedFoods.length}</span> món ăn</>
                ) : (
                  <span className="text-muted">Không tìm thấy món ăn nào</span>
                )}
              </h5>
              {(selectedCategory !== 'all' || searchTerm || priceRange.min > 0 || priceRange.max < maxPrice) && (
                <button 
                  className="btn btn-link p-0 text-decoration-none small"
                  onClick={() => {
                    setSelectedCategory('all');
                    setSearchTerm('');
                    setPriceRange({ min: 0, max: maxPrice });
                  }}
                >
                  <i className="fas fa-times me-1"></i>
                  Xóa tất cả bộ lọc
                </button>
              )}
            </div>
          </div>

          {/* Food Grid/List */}
          <div className={`row ${viewMode === 'list' ? 'g-3' : 'g-4'}`}>
            {filteredAndSortedFoods.length > 0 ? (
              filteredAndSortedFoods.map((food) => (
                <div 
                  key={food._id || food.id} 
                  className={viewMode === 'list' ? 'col-12' : 'col-lg-4 col-md-6'}
                >
                  <FoodCard food={food} viewMode={viewMode} />
                </div>
              ))
            ) : (
              <div className="col-12">
                <div className="text-center py-5">
                  <i className="fas fa-search fa-3x text-muted mb-3"></i>
                  <h4 className="text-muted">Không tìm thấy món ăn nào</h4>
                  <p className="text-muted">Hãy thử thay đổi bộ lọc hoặc từ khóa tìm kiếm</p>
                  <button 
                    className="btn btn-primary rounded-pill px-4"
                    onClick={() => {
                      setSelectedCategory('all');
                      setSearchTerm('');
                      setPriceRange({ min: 0, max: maxPrice });
                    }}
                  >
                    <i className="fas fa-redo me-2"></i>
                    Đặt lại bộ lọc
                  </button>
                </div>
              </div>
            )}
          </div>

          {/* Back to Top */}
          {filteredAndSortedFoods.length > 12 && (
            <div className="text-center mt-5">
              <button 
                className="btn btn-outline-primary rounded-pill px-4"
                onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
              >
                <i className="fas fa-arrow-up me-2"></i>
                Về đầu trang
              </button>
            </div>
          )}
        </div>
      </section>

      <Footer />

      {/* Floating Action Button */}
      <div className="position-fixed bottom-0 end-0 m-4" style={{ zIndex: 1050 }}>
        <button 
          className="btn btn-primary rounded-circle shadow-lg"
          style={{ width: '60px', height: '60px', fontSize: '1.5rem' }}
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
          title="Về đầu trang"
        >
          <i className="fas fa-chevron-up"></i>
        </button>
      </div>
    </>
  );
};

export default Menu;
