import { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const LoginSuccess = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const token = params.get("token");
    const tableId = params.get("tableId");

    if (token) {
      // Lưu token vào localStorage
      localStorage.setItem("token", token);

      // Lưu tableId nếu có
      if (tableId) {
        localStorage.setItem("tableId", tableId);
        // Điều hướng tới trang Menu với tableId
        navigate(`/menu/${tableId}`);
      } else {
        // Nếu không có tableId -> quay về trang menu chung
        navigate("/menu");
      }
    } else {
      // Nếu không có token -> quay về trang chủ
      navigate("/");
    }
  }, [navigate]);

  return (
    <div className="min-vh-100 d-flex align-items-center justify-content-center" 
         style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
      <div className="text-center text-white">
        <div className="spinner-border mb-3" style={{ width: '3rem', height: '3rem' }}>
          <span className="visually-hidden">Loading...</span>
        </div>
        <h4>Đăng nhập thành công!</h4>
        <p>Đang chuyển hướng...</p>
      </div>
    </div>
  );
};

export default LoginSuccess;
