import React, { useState, useEffect } from 'react';
import FoodCard from '../components/FoodCard';
import CategoryFilter from '../components/CategoryFilter';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/Menu.css'; 
import Header from '../components/header';  
import Footer from '../components/Footer';


const Menu = () => {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [loading, setLoading] = useState(true);

  // Mock data since API might not be available
  const mockFoods = [
    {
      id: 1,
      name: "Grilled Salmon",
      description: "Fresh Atlantic salmon grilled to perfection with herbs and lemon",
      price: 24.99,
      category: "main",
      image: "https://images.unsplash.com/photo-1467003909585-2f8a72700288?w=400&h=250&fit=crop"
    },
    {
      id: 2,
      name: "Chicken Caesar Salad",
      description: "Crispy romaine lettuce, grilled chicken, parmesan cheese, and caesar dressing",
      price: 16.99,
      category: "salad",
      image: "https://images.unsplash.com/photo-1546793665-c74683f339c1?w=400&h=250&fit=crop"
    },
    {
      id: 3,
      name: "Margherita Pizza",
      description: "Traditional Italian pizza with fresh mozzarella, tomatoes, and basil",
      price: 18.99,
      category: "pizza",
      image: "https://images.unsplash.com/photo-1513104890138-7c749659a591?w=400&h=250&fit=crop"
    },
    {
      id: 4,
      name: "Beef Burger",
      description: "Juicy beef patty with lettuce, tomato, cheese, and special sauce",
      price: 14.99,
      category: "burger",
      image: "https://images.unsplash.com/photo-1568901346375-23c9450c58cd?w=400&h=250&fit=crop"
    },
    {
      id: 5,
      name: "Chocolate Cake",
      description: "Rich chocolate cake with layers of chocolate ganache",
      price: 8.99,
      category: "dessert",
      image: "https://images.unsplash.com/photo-1578985545062-69928b1d9587?w=400&h=250&fit=crop"
    },
    {
      id: 6,
      name: "Pasta Carbonara",
      description: "Creamy pasta with bacon, eggs, and parmesan cheese",
      price: 19.99,
      category: "pasta",
      image: "https://images.unsplash.com/photo-1621996346565-e3dbc353d2e5?w=400&h=250&fit=crop"
    }
  ];

  const mockCategories = [
    { id: "main", name: "Main Course" },
    { id: "salad", name: "Salads" },
    { id: "pizza", name: "Pizza" },
    { id: "burger", name: "Burgers" },
    { id: "pasta", name: "Pasta" },
    { id: "dessert", name: "Desserts" }
  ];

  useEffect(() => {
    const loadData = async () => {
      try {
        // Try to load from API, fallback to mock data
        setFoods(mockFoods);
        setCategories(mockCategories);
      } catch (error) {
        console.error('Error loading data:', error);
        setFoods(mockFoods);
        setCategories(mockCategories);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  const filteredFoods = selectedCategory === 'all' 
    ? foods 
    : foods.filter(food => food.category === selectedCategory);

  if (loading) {
    return (
      <div className="container py-5">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <section id="menu" className="py-5">
      <div className="container">
        <div className="row">
          <div className="col-12 text-center mb-5">
            <h2 className="display-5 fw-bold">Our Menu</h2>
            <p className="lead">Discover our delicious selection of dishes</p>
          </div>
        </div>
        <Header />
        <CategoryFilter 
          categories={categories}
          selectedCategory={selectedCategory}
          onCategoryChange={setSelectedCategory}
        />

        <div className="row">
          {filteredFoods.map(food => (
            <FoodCard key={food.id} food={food} />
          ))}
        </div>
        <Footer />
      </div>
    </section>
  );
};

export default Menu;
