import React from 'react';
import { useCart } from '../contexts/CartContext';
import { Plus } from 'lucide-react';


const FoodCard = ({ food }) => {
  const { addToCart, isLoading } = useCart();

  return (
    <div className="col-md-6 col-lg-4 mb-4">
      <div className="card h-100 shadow-sm">
        <img 
          src={food.image || "https://images.unsplash.com/photo-1546069901-ba9599a7e63c?w=400&h=250&fit=crop"} 
          className="card-img-top" 
          alt={food.name}
          style={{ height: '200px', objectFit: 'cover' }}
        />
        <div className="card-body d-flex flex-column">
          <h5 className="card-title">{food.name}</h5>
          <p className="card-text text-muted flex-grow-1">{food.description}</p>
          <div className="d-flex justify-content-between align-items-center">
            <span className="h5 text-primary mb-0">${food.price}</span>
            <button 
              className="btn btn-primary"
              onClick={() => addToCart({
                id: food._id || food.id,
                name: food.name,
                price: food.price,
                image: food.image,
                quantity: 1
              })}
              disabled={isLoading}
            >
              <Plus size={16} className="me-1" />
              Add to Cart
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FoodCard;
