// pages/Checkout.jsx
import React, { useContext, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import { AuthContext } from '../contexts/AuthContext';
import { apiService } from '../service/apiService';

export default function Checkout() {
    const { tableId: paramTableId } = useParams();
    const { tableId: contextTableId, cartItems, clearCart, getTotalPrice } = useCart();
    const { user } = useContext(AuthContext);
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);

    const clean = val => (val && val !== 'null' && val !== 'undefined') ? val : '';
    const tableId = clean(paramTableId) || clean(contextTableId);

    const handlePay = async () => {
    if (!user?._id) {
        alert('Bạn cần đăng nhập để đặt hàng!');
        return;
    }

    if (!tableId) {
        alert('Không xác định được bàn để đặt hàng!');
        return;
    }

    if (!cartItems.length) {
        alert('Giỏ hàng đang trống!');
        return;
    }

    const orderData = {
        user: user._id,
        table: tableId,
        items: cartItems.map(item => ({
        foodId: item._id || item.id,
        quantity: item.quantity
        })),
        totalAmount: getTotalPrice(),
        status: 'Đang xử lý'
    };

    console.log('Gửi orderData:', orderData);   

    setLoading(true);
    try {
        await apiService.createOrder(orderData);
        clearCart();
        navigate('/success');
    } catch (error) {
        console.error('Lỗi khi thanh toán:', error.response?.data || error);
        alert('Thanh toán thất bại. Vui lòng thử lại.');
    } finally {
        setLoading(false);
    }
    };

  return (
    <div className="checkout-container">
      <h2>Thanh toán (Bàn {tableId})</h2>

      <ul>
        {cartItems.map(item => (
          <li key={item.id}>
            {item.name} x {item.quantity} = {item.price * item.quantity}đ
          </li>
        ))}
      </ul>

      <h4>Tổng cộng: {getTotalPrice()}đ</h4>

      <button
        onClick={handlePay}
        disabled={loading || !cartItems.length}
      >
        {loading ? 'Đang xử lý...' : 'Xác nhận và thanh toán'}
      </button>
    </div>
  );
}
