import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import FoodCard from '../components/FoodCard';
import CategoryFilter from '../components/CategoryFilter';
import { useCart } from '../contexts/CartContext';
import { cleanId } from '../utils/cleanId';
import 'bootstrap/dist/css/bootstrap.min.css';
import { apiService } from '../service/apiService';
import Header from '../components/Header';
import Footer from '../components/Footer';
import Hero from '../components/Hero';

const Menu = () => {
  const { tableId: paramTableId } = useParams();
  const navigate = useNavigate();
  const { tableId: contextTableId, setTableId } = useCart();

  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [priceRange, setPriceRange] = useState({ min: 0, max: 1000000 });
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [loading, setLoading] = useState(true);
  const [viewMode, setViewMode] = useState('grid'); // grid or list

  useEffect(() => {
    // Lấy giá trị tableId ưu tiên: URL -> Context -> localStorage
    const rawParam = paramTableId;
    const rawContext = contextTableId;
    const rawStorage = localStorage.getItem('tableId');
    const p = cleanId(rawParam);
    const c = cleanId(rawContext);
    const s = cleanId(rawStorage);
    const t = p || c || s;

    if (!t) {
      navigate('/scan');
      return;
    }

    setTableId(t);
    localStorage.setItem('tableId', t);

    // Tải dữ liệu menu
    const loadData = async () => {
      try {
        const [foodsData, categoriesData] = await Promise.all([
          apiService.getFoods(),
          apiService.getCategories(),
        ]);
        setFoods(foodsData);
        setCategories(categoriesData);
        
        // Tự động set price range dựa trên dữ liệu
        if (foodsData.length > 0) {
          const prices = foodsData.map(food => food.price);
          const minPrice = Math.min(...prices);
          const maxPrice = Math.max(...prices);
          setPriceRange({ min: minPrice, max: maxPrice });
        }
      } catch (error) {
        console.error('Lỗi khi tải menu:', error);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [paramTableId, contextTableId, navigate, setTableId]);

  // Filter và sort foods
  const filteredAndSortedFoods = React.useMemo(() => {
    let result = foods;

    // Filter by category
    if (selectedCategory !== 'all') {
      result = result.filter(food => food.category === selectedCategory);
    }

    // Filter by price range
    result = result.filter(food => 
      food.price >= priceRange.min && food.price <= priceRange.max
    );

    // Filter by search term
    if (searchTerm) {
      result = result.filter(food => 
        food.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        (food.description && food.description.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    // Sort
    result.sort((a, b) => {
      switch (sortBy) {
        case 'price-low':
          return a.price - b.price;
        case 'price-high':
          return b.price - a.price;
        case 'name':
          return a.name.localeCompare(b.name);
        default:
          return 0;
      }
    });

    return result;
  }, [foods, selectedCategory, priceRange, searchTerm, sortBy]);

  const maxPrice = foods.length > 0 ? Math.max(...foods.map(f => f.price)) : 1000000;

  if (loading) {
    return (
      <div className="min-vh-100 d-flex align-items-center justify-content-center" 
           style={{ background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' }}>
        <div className="text-center text-white">
          <div className="spinner-border mb-3" style={{ width: '3rem', height: '3rem' }}>
            <span className="visually-hidden">Loading...</span>
          </div>
          <h4>Đang tải thực đơn...</h4>
        </div>
      </div>
    );
  }

  return (
    <>
      <Header />
      <Hero />
      
      {/* Menu Section */}
      <section className="py-5" style={{ backgroundColor: '#f8f9fa' }}>
        <div className="container">
          {/* Header */}
          <div className="text-center mb-5">
            <h2 className="display-4 fw-bold text-dark mb-3">Thực đơn của chúng tôi</h2>
            <p className="lead text-muted">Khám phá những món ăn ngon với hương vị tuyệt vời</p>
            <div className="d-inline-block px-4 py-2 bg-primary text-white rounded-pill">
              <i className="fas fa-utensils me-2"></i>
              {filteredAndSortedFoods.length} món ăn
            </div>
          </div>

          {/* Filters */}
          <div className="card shadow-sm mb-4" style={{ borderRadius: '15px', border: 'none' }}>
            <div className="card-body p-4">
              <div className="row g-4">
                {/* Search */}
                <div className="col-lg-4">
                  <label className="form-label fw-semibold">
                    <i className="fas fa-search me-2 text-primary"></i>
                    Tìm kiếm món ăn
                  </label>
                  <div className="input-group">
                    <span className="input-group-text border-0 bg-light">
                      <i className="fas fa-search text-muted"></i>
                    </span>
                    <input
                      type="text"
                      className="form-control border-0 bg-light"
                      placeholder="Nhập tên món ăn..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                      style={{ borderRadius: '0 10px 10px 0' }}
                    />
                  </div>
                </div>

                {/* Category Filter */}
                <div className="col-lg-3">
                  <label className="form-label fw-semibold">
                    <i className="fas fa-tags me-2 text-primary"></i>
                    Danh mục
                  </label>
                  <select
                    className="form-select border-0 bg-light"
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    style={{ borderRadius: '10px' }}
                  >
                    <option value="all">Tất cả danh mục</option>
                    {categories.map(category => (
                      <option key={category._id || category.id} value={category._id || category.id}>
                        {category.name}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Sort */}
                <div className="col-lg-3">
                  <label className="form-label fw-semibold">
                    <i className="fas fa-sort me-2 text-primary"></i>
                    Sắp xếp theo
                  </label>
                  <select
                    className="form-select border-0 bg-light"
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    style={{ borderRadius: '10px' }}
                  >
                    <option value="name">Tên A-Z</option>
                    <option value="price-low">Giá thấp đến cao</option>
                    <option value="price-high">Giá cao đến thấp</option>
                  </select>
                </div>

                {/* View Mode */}
                <div className="col-lg-2">
                  <label className="form-label fw-semibold">
                    <i className="fas fa-eye me-2 text-primary"></i>
                    Hiển thị
                  </label>
                  <div className="btn-group w-100" role="group">
                    <button
                      className={`btn ${viewMode === 'grid' ? 'btn-primary' : 'btn-outline-primary'}`}
                      onClick={() => setViewMode('grid')}
                      title="Lưới"
                    >
                      <i className="fas fa-th"></i>
                    </button>
                    <button
                      className={`btn ${viewMode === 'list' ? 'btn-primary' : 'btn-outline-primary'}`}
                      onClick={() => setViewMode('list')}
                      title="Danh sách"
                    >
                      <i className="fas fa-list"></i>
                    </button>
                  </div>
                </div>
              </div>

              {/* Price Range Filter */}
              <div className="row mt-4">
                <div className="col-lg-6">
                  <label className="form-label fw-semibold">
                    <i className="fas fa-money-bill-wave me-2 text-primary"></i>
                    Khoảng giá: {priceRange.min.toLocaleString()}₫ - {priceRange.max.toLocaleString()}₫
                  </label>
                  <div className="row g-3">
                    <div className="col-6">
                      <input
                        type="range"
                        className="form-range"
                        min="0"
                        max={maxPrice}
                        step="10000"
                        value={priceRange.min}
                        onChange={(e) => setPriceRange(prev => ({ ...prev, min: parseInt(e.target.value) }))}
                      />
                      <small className="text-muted">Giá thấp nhất</small>
                    </div>
                    <div className="col-6">
                      <input
                        type="range"
                        className="form-range"
                        min="0"
                        max={maxPrice}
                        step="10000"
                        value={priceRange.max}
                        onChange={(e) => setPriceRange(prev => ({ ...prev, max: parseInt(e.target.value) }))}
                      />
                      <small className="text-muted">Giá cao nhất</small>
                    </div>
                  </div>
                </div>

                {/* Quick Filters */}
                <div className="col-lg-6">
                  <label className="form-label fw-semibold">
                    <i className="fas fa-filter me-2 text-primary"></i>
                    Lọc nhanh theo giá
                  </label>
                  <div className="d-flex flex-wrap gap-2">
                    <button 
                      className="btn btn-sm btn-outline-primary rounded-pill"
                      onClick={() => setPriceRange({ min: 0, max: 50000 })}
                    >
                      Dưới 50k
                    </button>
                    <button 
                      className="btn btn-sm btn-outline-primary rounded-pill"
                      onClick={() => setPriceRange({ min: 50000, max: 100000 })}
                    >
                      50k - 100k
                    </button>
                    <button 
                      className="btn btn-sm btn-outline-primary rounded-pill"
                      onClick={() => setPriceRange({ min: 100000, max: 200000 })}
                    >
                      100k - 200k
                    </button>
                    <button 
                      className="btn btn-sm btn-outline-primary rounded-pill"
                      onClick={() => setPriceRange({ min: 200000, max: maxPrice })}
                    >
                      Trên 200k
                    </button>
                    <button 
                      className="btn btn-sm btn-outline-secondary rounded-pill"
                      onClick={() => setPriceRange({ min: 0, max: maxPrice })}
                    >
                      <i className="fas fa-redo me-1"></i>
                      Reset
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Results */}
          <div className="d-flex justify-content-between align-items-center mb-4">
            <div>
              <h5 className="mb-0">
                {filteredAndSortedFoods.length > 0 ? (
                  <>Tìm thấy <span className="text-primary fw-bold">{filteredAndSortedFoods.length}</span> món ăn</>
                ) : (
                  <span className="text-muted">Không tìm thấy món ăn nào</span>
                )}
              </h5>
              {(selectedCategory !== 'all' || searchTerm || priceRange.min > 0 || priceRange.max < maxPrice) && (
                <button 
                  className="btn btn-link p-0 text-decoration-none small"
                  onClick={() => {
                    setSelectedCategory('all');
                    setSearchTerm('');
                    setPriceRange({ min: 0, max: maxPrice });
                  }}
                >
                  <i className="fas fa-times me-1"></i>
                  Xóa tất cả bộ lọc
                </button>
              )}
            </div>
          </div>

          {/* Food Grid/List */}
          <div className={`row ${viewMode === 'list' ? 'g-3' : 'g-4'}`}>
            {filteredAndSortedFoods.length > 0 ? (
              filteredAndSortedFoods.map((food) => (
                <div 
                  key={food._id || food.id} 
                  className={viewMode === 'list' ? 'col-12' : 'col-lg-4 col-md-6'}
                >
                  <FoodCard food={food} viewMode={viewMode} />
                </div>
              ))
            ) : (
              <div className="col-12">
                <div className="text-center py-5">
                  <i className="fas fa-search fa-3x text-muted mb-3"></i>
                  <h4 className="text-muted">Không tìm thấy món ăn nào</h4>
                  <p className="text-muted">Hãy thử thay đổi bộ lọc hoặc từ khóa tìm kiếm</p>
                  <button 
                    className="btn btn-primary rounded-pill px-4"
                    onClick={() => {
                      setSelectedCategory('all');
                      setSearchTerm('');
                      setPriceRange({ min: 0, max: maxPrice });
                    }}
                  >
                    <i className="fas fa-redo me-2"></i>
                    Đặt lại bộ lọc
                  </button>
                </div>
              </div>
            )}
          </div>

          {/* Back to Top */}
          {filteredAndSortedFoods.length > 12 && (
            <div className="text-center mt-5">
              <button 
                className="btn btn-outline-primary rounded-pill px-4"
                onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
              >
                <i className="fas fa-arrow-up me-2"></i>
                Về đầu trang
              </button>
            </div>
          )}
        </div>
      </section>

      <Footer />

      {/* Floating Action Button */}
      <div className="position-fixed bottom-0 end-0 m-4" style={{ zIndex: 1050 }}>
        <button 
          className="btn btn-primary rounded-circle shadow-lg"
          style={{ width: '60px', height: '60px', fontSize: '1.5rem' }}
          onClick={() => window.scrollTo({ top: 0, behavior: 'smooth' })}
          title="Về đầu trang"
        >
          <i className="fas fa-chevron-up"></i>
        </button>
      </div>
    </>
  );
};

export default Menu;
