import React, { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight, Star, Clock, MapPin } from 'lucide-react';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      id: 1,
      image: "https://images.unsplash.com/photo-1504674900247-0877df9cc836?w=800&h=600&fit=crop",
      title: "Món gì cũng có",
      subtitle: "Trải nghiệm ẩm thực hảo hạng",
      description: "Nguyên liệu tươi ngon, chế biến chuyên nghiệp và được phục vụ tận bàn với chất lượng 5 sao.",
      accent: "🍽️"
    },
    {
      id: 2,
      image: "https://images.unsplash.com/photo-1567620905732-2d1ec7ab7445?w=800&h=600&fit=crop",
      title: "Ẩm thực đặc sắc",
      subtitle: "Hương vị Việt Nam authentic",
      description: "Khám phá những món ăn truyền thống được chế biến bằng bí quyết gia truyền độc đáo.",
      accent: "🇻🇳"
    },
    {
      id: 3,
      image: "https://images.unsplash.com/photo-1559339352-11d035aa65de?w=800&h=600&fit=crop",
      title: "Không gian sang trọng",
      subtitle: "Phục vụ chuyên nghiệp",
      description: "Thưởng thức bữa ăn trong không gian ấm cúng với dịch vụ tận tâm từ đội ngũ của chúng tôi.",
      accent: "✨"
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  // Auto-advance carousel
  useEffect(() => {
    const timer = setInterval(nextSlide, 5000);
    return () => clearInterval(timer);
  }, []);

  return (
    <div className="relative min-h-screen bg-gradient-to-br from-amber-50 via-orange-50 to-red-50 overflow-hidden">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-5">
        <div className="absolute top-20 left-20 w-32 h-32 bg-amber-400 rounded-full blur-3xl"></div>
        <div className="absolute bottom-20 right-20 w-48 h-48 bg-orange-400 rounded-full blur-3xl"></div>
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-64 h-64 bg-red-300 rounded-full blur-3xl"></div>
      </div>

      {/* Carousel Container */}
      <div className="relative h-screen flex items-center">
        <div className="container mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid lg:grid-cols-2 gap-8 lg:gap-12 items-center">
            
            {/* Content Side */}
            <div className="relative z-10 text-center lg:text-left space-y-6 lg:space-y-8">
              {/* Floating Accent */}
              <div className="text-6xl sm:text-7xl lg:text-8xl mb-4 animate-bounce">
                {slides[currentSlide].accent}
              </div>
              
              {/* Main Title */}
              <div className="space-y-2">
                <h1 className="text-4xl sm:text-5xl lg:text-6xl xl:text-7xl font-bold bg-gradient-to-r from-amber-600 via-orange-600 to-red-600 bg-clip-text text-transparent leading-tight">
                  {slides[currentSlide].title}
                </h1>
                <h2 className="text-xl sm:text-2xl lg:text-3xl text-gray-700 font-medium">
                  {slides[currentSlide].subtitle}
                </h2>
              </div>

              {/* Description */}
              <p className="text-gray-600 text-base sm:text-lg lg:text-xl leading-relaxed max-w-2xl mx-auto lg:mx-0">
                {slides[currentSlide].description}
              </p>

              {/* Features */}
              <div className="flex flex-wrap justify-center lg:justify-start gap-4 text-sm sm:text-base">
                <div className="flex items-center gap-2 bg-white/70 px-4 py-2 rounded-full backdrop-blur-sm">
                  <Star className="w-4 h-4 text-amber-500 fill-current" />
                  <span className="text-gray-700">5 sao chất lượng</span>
                </div>
                <div className="flex items-center gap-2 bg-white/70 px-4 py-2 rounded-full backdrop-blur-sm">
                  <Clock className="w-4 h-4 text-green-500" />
                  <span className="text-gray-700">Mở cửa 24/7</span>
                </div>
                <div className="flex items-center gap-2 bg-white/70 px-4 py-2 rounded-full backdrop-blur-sm">
                  <MapPin className="w-4 h-4 text-red-500" />
                  <span className="text-gray-700">Giao hàng nhanh</span>
                </div>
              </div>

              {/* CTA Buttons */}
              <div className="flex flex-col sm:flex-row gap-4 justify-center lg:justify-start">
                <button className="bg-gradient-to-r from-amber-500 to-orange-500 hover:from-amber-600 hover:to-orange-600 text-white px-8 py-4 rounded-full text-lg font-semibold shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-300">
                  Xem Menu Ngay
                </button>
                <button className="border-2 border-amber-500 text-amber-600 hover:bg-amber-500 hover:text-white px-8 py-4 rounded-full text-lg font-semibold transition-all duration-300">
                  Đặt Bàn Ngay
                </button>
              </div>

              {/* Slide Indicators */}
              <div className="flex justify-center lg:justify-start gap-2 pt-4">
                {slides.map((_, index) => (
                  <button
                    key={index}
                    onClick={() => setCurrentSlide(index)}
                    className={`w-3 h-3 rounded-full transition-all duration-300 ${
                      index === currentSlide 
                        ? 'bg-amber-500 w-8' 
                        : 'bg-gray-300 hover:bg-gray-400'
                    }`}
                  />
                ))}
              </div>
            </div>

            {/* Image Side */}
            <div className="relative lg:order-last">
              <div className="relative h-80 sm:h-96 lg:h-[500px] xl:h-[600px] rounded-3xl overflow-hidden shadow-2xl">
                {/* Main Image */}
                <div className="relative w-full h-full">
                  <img 
                    src={slides[currentSlide].image}
                    alt={slides[currentSlide].title}
                    className="w-full h-full object-cover transition-all duration-1000 ease-in-out transform hover:scale-110"
                  />
                  
                  {/* Gradient Overlay */}
                  <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent"></div>
                  
                  {/* Floating Elements */}
                  <div className="absolute top-4 right-4 bg-white/90 backdrop-blur-sm px-3 py-2 rounded-full">
                    <div className="flex items-center gap-1">
                      <Star className="w-4 h-4 text-yellow-400 fill-current" />
                      <span className="text-sm font-semibold">4.9</span>
                    </div>
                  </div>
                </div>

                {/* Navigation Arrows */}
                <button 
                  onClick={prevSlide}
                  className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-white/80 hover:bg-white text-gray-700 p-3 rounded-full shadow-lg backdrop-blur-sm transition-all duration-300 hover:scale-110"
                >
                  <ChevronLeft className="w-5 h-5" />
                </button>
                <button 
                  onClick={nextSlide}
                  className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-white/80 hover:bg-white text-gray-700 p-3 rounded-full shadow-lg backdrop-blur-sm transition-all duration-300 hover:scale-110"
                >
                  <ChevronRight className="w-5 h-5" />
                </button>
              </div>

              {/* Decorative Elements */}
