import React, { useState, useEffect } from 'react';
import { ChevronLeft, ChevronRight, Star, Clock, MapPin } from 'lucide-react';

const Hero = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    {
      id: 1,
      image: "https://images.unsplash.com/photo-1504674900247-0877df9cc836?w=800&h=600&fit=crop",
      title: "Món gì cũng có",
      subtitle: "Trải nghiệm ẩm thực hảo hạng",
      description: "Nguyên liệu tươi ngon, chế biến chuyên nghiệp và được phục vụ tận bàn với chất lượng 5 sao.",
      accent: "🍽️"
    },
    {
      id: 2,
      image: "https://images.unsplash.com/photo-1567620905732-2d1ec7ab7445?w=800&h=600&fit=crop",
      title: "Ẩm thực đặc sắc",
      subtitle: "Hương vị Việt Nam authentic",
      description: "Khám phá những món ăn truyền thống được chế biến bằng bí quyết gia truyền độc đáo.",
      accent: "🇻🇳"
    },
    {
      id: 3,
      image: "https://images.unsplash.com/photo-1559339352-11d035aa65de?w=800&h=600&fit=crop",
      title: "Không gian sang trọng",
      subtitle: "Phục vụ chuyên nghiệp",
      description: "Thưởng thức bữa ăn trong không gian ấm cúng với dịch vụ tận tâm từ đội ngũ của chúng tôi.",
      accent: "✨"
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  // Auto-advance carousel
  useEffect(() => {
    const timer = setInterval(nextSlide, 5000);
    return () => clearInterval(timer);
  }, []);

  const customStyles = {
    heroSection: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #fef7cd 0%, #fed7aa 50%, #fecaca 100%)',
      position: 'relative',
      overflow: 'hidden'
    },
    backgroundPattern: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      opacity: 0.05,
      pointerEvents: 'none'
    },
    floatingElement1: {
      position: 'absolute',
      top: '80px',
      left: '80px',
      width: '128px',
      height: '128px',
      background: '#f59e0b',
      borderRadius: '50%',
      filter: 'blur(48px)'
    },
    floatingElement2: {
      position: 'absolute',
      bottom: '80px',
      right: '80px',
      width: '192px',
      height: '192px',
      background: '#fb923c',
      borderRadius: '50%',
      filter: 'blur(48px)'
    },
    floatingElement3: {
      position: 'absolute',
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      width: '256px',
      height: '256px',
      background: '#fca5a5',
      borderRadius: '50%',
      filter: 'blur(48px)'
    },
    gradientText: {
      background: 'linear-gradient(to right, #d97706, #ea580c, #dc2626)',
      WebkitBackgroundClip: 'text',
      WebkitTextFillColor: 'transparent',
      backgroundClip: 'text'
    },
    imageContainer: {
      height: '500px',
      borderRadius: '24px',
      overflow: 'hidden',
      position: 'relative',
      boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.25)'
    },
    imageOverlay: {
      position: 'absolute',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      background: 'linear-gradient(to top, rgba(0,0,0,0.2) 0%, transparent 100%)'
    },
    navButton: {
      position: 'absolute',
      top: '50%',
      transform: 'translateY(-50%)',
      backgroundColor: 'rgba(255, 255, 255, 0.9)',
      border: 'none',
      borderRadius: '50%',
      width: '48px',
      height: '48px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      backdropFilter: 'blur(4px)'
    },
    leftNav: {
      left: '16px'
    },
    rightNav: {
      right: '16px'
    },
    featureBadge: {
      backgroundColor: 'rgba(255, 255, 255, 0.8)',
      backdropFilter: 'blur(4px)',
      borderRadius: '25px'
    },
    gradientButton: {
      background: 'linear-gradient(to right, #f59e0b, #fb923c)',
      border: 'none',
      borderRadius: '25px',
      transition: 'all 0.3s ease'
    },
    outlineButton: {
      border: '2px solid #f59e0b',
      borderRadius: '25px',
      backgroundColor: 'transparent',
      color: '#d97706',
      transition: 'all 0.3s ease'
    },
    indicator: {
      width: '12px',
      height: '12px',
      borderRadius: '50%',
      backgroundColor: '#d1d5db',
      border: 'none',
      cursor: 'pointer',
      transition: 'all 0.3s ease'
    },
    activeIndicator: {
      width: '32px',
      backgroundColor: '#f59e0b'
    },
    ratingBadge: {
      position: 'absolute',
      top: '16px',
      right: '16px',
      backgroundColor: 'rgba(255, 255, 255, 0.95)',
      backdropFilter: 'blur(4px)',
      borderRadius: '25px'
    },
    decorativeElement1: {
      position: 'absolute',
      top: '-24px',
      left: '-24px',
      width: '48px',
      height: '48px',
      backgroundColor: '#f59e0b',
      borderRadius: '50%',
      animation: 'pulse 2s infinite'
    },
    decorativeElement2: {
      position: 'absolute',
      bottom: '-16px',
      right: '-16px',
      width: '32px',
      height: '32px',
      backgroundColor: '#fb923c',
      borderRadius: '50%',
      animation: 'ping 1s infinite'
    },
    accentEmoji: {
      fontSize: '4rem',
      animation: 'bounce 1s infinite'
    },
    waveBottom: {
      position: 'absolute',
      bottom: 0,
      left: 0,
      width: '100%'
    }
  };

  // Add keyframe animations
  useEffect(() => {
    const style = document.createElement('style');
    style.textContent = `
      @keyframes pulse {
        0%, 100% { transform: scale(1); opacity: 1; }
        50% { transform: scale(1.05); opacity: 0.8; }
      }
      @keyframes ping {
        0% { transform: scale(1); opacity: 1; }
        75%, 100% { transform: scale(2); opacity: 0; }
      }
      @keyframes bounce {
        0%, 100% { transform: translateY(0); }
        50% { transform: translateY(-10px); }
      }
      .hero-image:hover {
        transform: scale(1.05);
        transition: transform 0.5s ease;
      }
      .btn-hover:hover {
        transform: scale(1.05);
        box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.1);
      }
      .nav-btn:hover {
        transform: translateY(-50%) scale(1.1);
        background-color: white;
      }
    `;
    document.head.appendChild(style);
    
    return () => {
      document.head.removeChild(style);
    };
  }, []);

  return (
    <div style={customStyles.heroSection}>
      {/* Background Pattern */}
      <div style={customStyles.backgroundPattern}>
        <div style={customStyles.floatingElement1}></div>
        <div style={customStyles.floatingElement2}></div>
        <div style={customStyles.floatingElement3}></div>
      </div>

      {/* Main Content */}
      <div className="container-fluid h-100 d-flex align-items-center">
        <div className="container">
          <div className="row align-items-center min-vh-100">
            
            {/* Content Column */}
            <div className="col-lg-6 text-center text-lg-start mb-5 mb-lg-0">
              {/* Floating Accent */}
              <div style={customStyles.accentEmoji} className="mb-4">
                {slides[currentSlide].accent}
              </div>
              
              {/* Main Title */}
              <div className="mb-4">
                <h1 className="display-1 fw-bold mb-3" style={customStyles.gradientText}>
                  {slides[currentSlide].title}
                </h1>
                <h2 className="h3 text-muted fw-normal">
                  {slides[currentSlide].subtitle}
                </h2>
              </div>

              {/* Description */}
              <p className="lead text-muted mb-4" style={{maxWidth: '600px'}}>
                {slides[currentSlide].description}
              </p>

              {/* Features */}
              <div className="d-flex flex-wrap justify-content-center justify-content-lg-start gap-3 mb-4">
                <div className="d-flex align-items-center px-3 py-2" style={customStyles.featureBadge}>
                  <Star className="me-2" style={{width: '16px', height: '16px', color: '#f59e0b'}} fill="currentColor" />
                  <span className="text-muted small">5 sao chất lượng</span>
                </div>
                <div className="d-flex align-items-center px-3 py-2" style={customStyles.featureBadge}>
                  <Clock className="me-2" style={{width: '16px', height: '16px', color: '#22c55e'}} />
                  <span className="text-muted small">Mở cửa 24/7</span>
                </div>
                <div className="d-flex align-items-center px-3 py-2" style={customStyles.featureBadge}>
                  <MapPin className="me-2" style={{width: '16px', height: '16px', color: '#ef4444'}} />
                  <span className="text-muted small">Giao hàng nhanh</span>
                </div>
              </div>

              {/* CTA Buttons */}
              <div className="d-flex flex-column flex-sm-row gap-3 justify-content-center justify-content-lg-start mb-4">
                <button className="btn btn-lg text-white px-4 py-3 btn-hover" style={customStyles.gradientButton}>
                  <span className="fw-semibold">Xem Menu Ngay</span>
                </button>
                <button className="btn btn-lg px-4 py-3 btn-hover" style={customStyles.outlineButton}>
                  <span className="fw-semibold">Đặt Bàn Ngay</span>
                </button>
              </div>

              {/* Slide Indicators */}
              <div className="d-flex justify-content-center justify-content-lg-start gap-2">
                {slides.map((_, index) => (
                  <button
                    key={index}
                    onClick={() => setCurrentSlide(index)}
                    style={{
                      ...customStyles.indicator,
                      ...(index === currentSlide ? customStyles.activeIndicator : {})
                    }}
                  />
                ))}
              </div>
            </div>

            {/* Image Column */}
            <div className="col-lg-6">
              <div className="position-relative">
                <div style={customStyles.imageContainer}>
                  {/* Main Image */}
                  <img 
                    src={slides[currentSlide].image}
                    alt={slides[currentSlide].title}
                    className="w-100 h-100 hero-image"
                    style={{objectFit: 'cover', transition: 'all 1s ease'}}
                  />
                  
                  {/* Gradient Overlay */}
                  <div style={customStyles.imageOverlay}></div>
                  
                  {/* Rating Badge */}
                  <div className="px-3 py-2" style={customStyles.ratingBadge}>
                    <div className="d-flex align-items-center">
                      <Star style={{width: '16px', height: '16px', color: '#fbbf24'}} fill="currentColor" />
                      <span className="ms-1 fw-semibold small">4.9</span>
                    </div>
                  </div>

                  {/* Navigation Arrows */}
                  <button 
                    onClick={prevSlide}
                    className="nav-btn"
                    style={{...customStyles.navButton, ...customStyles.leftNav}}
                  >
                    <ChevronLeft style={{width: '20px', height: '20px', color: '#374151'}} />
                  </button>
                  <button 
                    onClick={nextSlide}
                    className="nav-btn"
                    style={{...customStyles.navButton, ...customStyles.rightNav}}
                  >
                    <ChevronRight style={{width: '20px', height: '20px', color: '#374151'}} />
                  </button>
                </div>

                {/* Decorative Elements */}
                <div style={customStyles.decorativeElement1}></div>
                <div style={customStyles.decorativeElement2}></div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Bottom Wave */}
      <div style={customStyles.waveBottom}>
        <svg viewBox="0 0 1440 120" className="w-100" style={{height: '80px'}}>
          <path 
            fill="rgb(255, 255, 255)" 
            d="M0,64L48,69.3C96,75,192,85,288,85.3C384,85,480,75,576,64C672,53,768,43,864,48C960,53,1056,75,1152,80C1248,85,1344,75,1392,69.3L1440,64L1440,120L1392,120C1344,120,1248,120,1152,120C1056,120,960,120,864,120C768,120,672,120,576,120C480,120,384,120,288,120C192,120,96,120,48,120L0,120Z"
          />
        </svg>
      </div>
    </div>
  );
};

export default Hero;
