// pages/Menu.jsx
import React, { useEffect, useState, useContext } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { apiService } from '../service/apiService'
import { CartContext } from '../contexts/CartContext'

export default function Menu() {
  const { tableId } = useParams()
  const navigate = useNavigate()
  const { addToCart } = useContext(CartContext)
  const [foods, setFoods] = useState([])

  useEffect(() => {
    if (!tableId) return navigate('/')
    apiService.getFoods().then(setFoods)
  }, [tableId, navigate])

  const handleAdd = async (food) => {
    // trong CartContext, bạn có thể lưu cả tableId
    await addToCart({ foodId: food._id, quantity: 1, tableId })
  }

  const gotoCheckout = () => {
    navigate(`/checkout/${tableId}`)
  }

  return (
    <div>
      <h2>Thực đơn (Bàn {tableId})</h2>
      <div className="grid grid-cols-3 gap-4">
        {foods.map(f => (
          <div key={f._id} className="p-2 border rounded">
            <img src={f.image} alt={f.name} className="h-32 object-cover" />
            <h3>{f.name}</h3>
            <p>{f.price.toLocaleString()}₫</p>
            <button onClick={() => handleAdd(f)}>+ Giỏ hàng</button>
          </div>
        ))}
      </div>
      <button onClick={gotoCheckout} className="mt-4 btn-primary">
        Thanh toán
      </button>
    </div>
  )
}
