// pages/Checkout.jsx
import React, { useContext, useState } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import { useCart  } from '../contexts/CartContext'
import { AuthContext } from '../contexts/AuthContext'
import { apiService } from '../service/apiService'

export default function Checkout() {
  const { tableId: paramTableId } = useParams()
  const { tableId: contextTableId } = useCart()
  const { user } = useContext(AuthContext)
  const navigate = useNavigate()
  const { items, clearCart } = useContext(CartContext)
  const [loading, setLoading] = useState(false)

  // Xác định tableId ưu tiên param, fallback context
  const tableId = paramTableId || contextTableId

  const handlePay = async () => {
    if (!user?._id) {
      alert('Bạn cần đăng nhập để đặt hàng!')
      return
    }
    setLoading(true)
    try {
      const orderData = {
        user: user._id,
        table: tableId,
        items: items.map(i => ({ foodId: i.foodId, quantity: i.quantity })),
        totalAmount: items.reduce((sum, i) => sum + i.price * i.quantity, 0),
        status: 'pending'
      }

      await apiService.createOrder(orderData)
      clearCart()
      navigate('/success')
    } catch (err) {
      console.error(err)
      alert('Thanh toán thất bại')
    } finally {
      setLoading(false)
    }
  }

  return (
    <div>
      <h2>Thanh toán (Bàn {tableId})</h2>
      {/* ... phần render giỏ hàng */}
      <button onClick={handlePay} disabled={loading || !items.length}>
        {loading ? 'Đang xử lý...' : 'Xác nhận và thanh toán'}
      </button>
    </div>
  )
}
