import React, { useState, useEffect } from 'react';
import FoodCard from '../components/FoodCard';
import CategoryFilter from '../components/CategoryFilter';
import 'bootstrap/dist/css/bootstrap.min.css';
import {apiService} from "../service/apiService" 
import Header from '../components/Header';
import Footer from '../components/Footer';


const Menu = () => {
  const [foods, setFoods] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadData = async () => {
      try {
        const [foodsData, categoriesData] = await Promise.all([
          apiService.getFoods(),
          apiService.getCategories()
        ]);

        setFoods(foodsData);
        setCategories(categoriesData);
      } catch (error) {
        console.error('Lỗi khi tải menu:', error);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  const filteredFoods =
    selectedCategory === 'all'
      ? foods
      : foods.filter(food => food.category === selectedCategory);

  if (loading) {
    return (
      <div className="container py-5">
        <div className="text-center">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <section id="menu" className="py-5">
      <div className="container">
        <div className="row">
          <div className="col-12 text-center mb-5">
            <h2 className="display-5 fw-bold">Our Menu</h2>
            <p className="lead">Discover our delicious selection of dishes</p>
          </div>
        </div>

        <CategoryFilter
          categories={[{ id: 'all', name: 'All' }, ...categories]}
          selectedCategory={selectedCategory}
          onCategoryChange={setSelectedCategory}
        />

        <div className="row mt-4">
          {filteredFoods.length > 0 ? (
            filteredFoods.map(food => <FoodCard key={food._id || food.id} food={food} />)
          ) : (
            <p className="text-muted text-center">No food items found.</p>
          )}
        </div>
      </div>
    </section>
  );
};

export default Menu;
