// pages/CartModal.jsx
import React, { useState, useContext } from 'react';
import { useCart } from '../contexts/CartContext';
import { AuthContext } from '../contexts/AuthContext';
import { Plus, Minus, X } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import bootstrap from 'bootstrap/dist/js/bootstrap.bundle.min.js';
import 'bootstrap/dist/css/bootstrap.min.css';

const CartModal = () => {
  const {
    cartItems,
    removeFromCart,
    updateQuantity,
    clearCart,
    getTotalPrice,
    tableId: contextTableId,
  } = useCart();

  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [isOrdering, setIsOrdering] = useState(false);

  const handleCheckout = () => {
    if (!user?._id) {
      alert('Bạn cần đăng nhập để đặt hàng!');
      return;
    }
    // Nếu giỏ trống cũng không cho đi tiếp
    if (cartItems.length === 0) {
      alert('Giỏ hàng của bạn đang trống!');
      return;
    }

    // Redirect tới trang checkout cùng tableId
    const table = contextTableId || localStorage.getItem('tableId');
    navigate(`/checkout/${table}`);
    
    // Ẩn modal
    const modalEl = document.getElementById('cartModal');
    const bsModal = bootstrap.Modal.getInstance(modalEl);
    bsModal?.hide();
  };

  return (
    <div className="modal fade" id="cartModal" tabIndex="-1">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Giỏ hàng</h5>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="modal"
            />
          </div>
          <div className="modal-body">
            {cartItems.length === 0 ? (
              <p className="text-center text-muted">
                Giỏ hàng của bạn đang trống
              </p>
            ) : (
              <>
                {cartItems.map(item => (
                  <div
                    key={item.id}
                    className="d-flex align-items-center border-bottom pb-3 mb-3"
                  >
                    <img
                      src={item.image}
                      alt={item.name}
                      className="rounded me-3"
                      style={{
                        width: '60px',
                        height: '60px',
                        objectFit: 'cover'
                      }}
                    />
                    <div className="flex-grow-1">
                      <h6 className="mb-1">{item.name}</h6>
                      <p className="text-muted mb-0">
                        {item.price.toLocaleString()}₫
                      </p>
                    </div>
                    <div className="d-flex align-items-center me-3">
                      <button
                        className="btn btn-sm btn-outline-secondary"
                        onClick={() =>
                          updateQuantity(item.id, item.quantity - 1)
                        }
                      >
                        <Minus size={14} />
                      </button>
                      <span className="mx-2">{item.quantity}</span>
                      <button
                        className="btn btn-sm btn-outline-secondary"
                        onClick={() =>
                          updateQuantity(item.id, item.quantity + 1)
                        }
                      >
                        <Plus size={14} />
                      </button>
                    </div>
                    <button
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => removeFromCart(item.id)}
                    >
                      <X size={14} />
                    </button>
                  </div>
                ))}
                <div className="d-flex justify-content-between align-items-center">
                  <h5>
                    Tổng cộng: {getTotalPrice().toLocaleString()}₫
                  </h5>
                  <button
                    className="btn btn-outline-secondary"
                    onClick={clearCart}
                  >
                    Xoá giỏ hàng
                  </button>
                </div>
              </>
            )}
          </div>
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-secondary"
              data-bs-dismiss="modal"
            >
              Tiếp tục mua sắm
            </button>
            {cartItems.length > 0 && (
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleCheckout}
                disabled={isOrdering}
              >
                Thanh toán
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartModal;
