import React from 'react';
import { useCart } from '../contexts/CartContext';
import { Plus } from 'lucide-react';
import { motion } from 'framer-motion';
import { useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext'; // đúng đường dẫn của bạn
import { useNavigate, useLocation } from 'react-router-dom';

const FoodCard = ({ food }) => {
  const { addToCart, isLoading } = useCart();
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const location = useLocation();

  const handleAddToCart = () => {
    if (!user) {
      // Lấy tableId từ URL hoặc localStorage
      const tableIdFromUrl = location.pathname.split('/menu/')[1];
      const tableIdFromStorage = localStorage.getItem('tableId');
      const currentTableId = tableIdFromUrl || tableIdFromStorage;

      // Lưu URL hiện tại và tableId để chuyển hướng lại sau đăng nhập
      navigate('/login', {
        state: { 
          from: location,
          tableId: currentTableId 
        },
      });
      return;
    }

    addToCart({
      id: food._id || food.id,
      name: food.name,
      price: food.price,
      image: food.image,
      quantity: 1,
    });
  };

  return (
    <motion.div
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
      className="col-md-6 col-lg-4 mb-4"
    >
      <div className="card h-100 shadow-sm rounded">
        <div className="position-relative">
          <img
            src={food.image}
            className="card-img-top"
            alt={food.name}
            style={{ height: '200px', objectFit: 'cover' }}
          />
          <span className="badge bg-danger position-absolute top-0 start-0 m-2">
            {food.price.toLocaleString()}₫
          </span>
        </div>
        <div className="card-body d-flex flex-column">
          <h5 className="card-title text-truncate">{food.name}</h5>
          <p
            className="card-text text-muted flex-grow-1 text-truncate"
            style={{
              WebkitLineClamp: 3,
              display: '-webkit-box',
              WebkitBoxOrient: 'vertical',
              overflow: 'hidden',
            }}
          >
            {food.description}
          </p>
          <button
            className="btn btn-success mt-3 d-flex align-items-center justify-content-center"
            onClick={handleAddToCart}
            disabled={isLoading}
          >
            {isLoading ? (
              <span
                className="spinner-border spinner-border-sm me-2"
                role="status"
                aria-hidden="true"
              ></span>
            ) : (
              <Plus size={16} className="me-2" />
            )}
            {isLoading ? 'Adding...' : 'Add to Cart'}
          </button>
        </div>
      </div>
    </motion.div>
  );
};

export default FoodCard;
