// pages/CartModal.jsx
import React, { useState, useContext } from 'react';
import { useCart } from '../contexts/CartContext';
import { Plus, Minus, X } from 'lucide-react';
import { apiService } from '../service/apiService';
import { AuthContext } from '../contexts/AuthContext'; 
import bootstrap from 'bootstrap/dist/js/bootstrap.bundle.min.js';
import 'bootstrap/dist/css/bootstrap.min.css';

const CartModal = () => {
  const {
    cartItems,
    removeFromCart,
    updateQuantity,
    clearCart,
    getTotalPrice,
    tableId: contextTableId,  // bạn lưu tableId trong CartContext
  } = useCart();
  const { user } = useContext(AuthContext);
  const [isOrdering, setIsOrdering] = useState(false);

  const handleOrder = async () => {
    setIsOrdering(true);
    try {
      // Lấy userId
      const user = JSON.parse(localStorage.getItem('user'));
      if (!user?._id) {
        alert('Bạn cần đăng nhập để đặt hàng!');
        setIsOrdering(false);
        return;
      }

      // Lấy tableId: ưu tiên từ context, fallback về localStorage
      const table =
        contextTableId ||
        localStorage.getItem('tableId');
      if (!table) {
        alert('Không xác định được số bàn.');
        setIsOrdering(false);
        return;
      }

      // Chuẩn data đúng format backend
      const orderData = {
        user: user._id,
        table: contextTableId || localStorage.getItem('tableId'),
        items: cartItems.map(item => ({
          foodId: item.id,
          quantity: item.quantity
        })),
        totalAmount: getTotalPrice(),
        status: 'pending'
      };

      // Gọi API
      await apiService.createOrder(orderData);

      alert('Đặt hàng thành công!');
      clearCart();

      // Ẩn modal
      const modalEl = document.getElementById('cartModal');
      const bsModal = bootstrap.Modal.getInstance(modalEl);
      bsModal?.hide();
    } catch (error) {
      console.error('Lỗi khi đặt hàng:', error);
      alert('Đặt hàng thất bại. Vui lòng thử lại sau.');
    } finally {
      setIsOrdering(false);
    }
  };

  return (
    <div className="modal fade" id="cartModal" tabIndex="-1">
      <div className="modal-dialog modal-lg">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title">Giỏ hàng</h5>
            <button
              type="button"
              className="btn-close"
              data-bs-dismiss="modal"
            ></button>
          </div>
          <div className="modal-body">
            {cartItems.length === 0 ? (
              <p className="text-center text-muted">
                Giỏ hàng của bạn đang trống
              </p>
            ) : (
              <>
                {cartItems.map(item => (
                  <div
                    key={item.id}
                    className="d-flex align-items-center border-bottom pb-3 mb-3"
                  >
                    <img
                      src={item.image}
                      alt={item.name}
                      className="rounded me-3"
                      style={{
                        width: '60px',
                        height: '60px',
                        objectFit: 'cover'
                      }}
                    />
                    <div className="flex-grow-1">
                      <h6 className="mb-1">{item.name}</h6>
                      <p className="text-muted mb-0">
                        {item.price.toLocaleString()}₫
                      </p>
                    </div>
                    <div className="d-flex align-items-center me-3">
                      <button
                        className="btn btn-sm btn-outline-secondary"
                        onClick={() =>
                          updateQuantity(item.id, item.quantity - 1)
                        }
                      >
                        <Minus size={14} />
                      </button>
                      <span className="mx-2">{item.quantity}</span>
                      <button
                        className="btn btn-sm btn-outline-secondary"
                        onClick={() =>
                          updateQuantity(item.id, item.quantity + 1)
                        }
                      >
                        <Plus size={14} />
                      </button>
                    </div>
                    <button
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => removeFromCart(item.id)}
                    >
                      <X size={14} />
                    </button>
                  </div>
                ))}
                <div className="d-flex justify-content-between align-items-center">
                  <h5>
                    Tổng cộng:{' '}
                    {getTotalPrice().toLocaleString()}₫
                  </h5>
                  <button
                    className="btn btn-outline-secondary"
                    onClick={clearCart}
                  >
                    Xoá giỏ hàng
                  </button>
                </div>
              </>
            )}
          </div>
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-secondary"
              data-bs-dismiss="modal"
            >
              Tiếp tục mua sắm
            </button>
            {cartItems.length > 0 && (
              <button
                type="button"
                className="btn btn-primary"
                onClick={handleOrder}
                disabled={isOrdering}
              >
                {isOrdering ? 'Đang đặt...' : 'Thanh toán'}
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartModal;
